mapper.update:
update orders 
set delivery_active = 1 , purchase_date = sysdate() 
where order_idx = #{order_idx}

update users 
set u_phone = #{u_phone}, u_email = #{u_email} 
where u_idx = #{u_idx}

update address 
set basic = 1 
where addr_idx = #{addr_idx}

update users 
set active = 0 
where u_idx = #{u_idx}
